import { Paper, Typography, makeStyles } from "@material-ui/core";
import CheckBox from "@material-ui/core/Checkbox";
import React from 'react';
const useStyle = makeStyles({
    root:{
        width:'100%',
        margin:'5px 0',
        display:'flex',
        justifyContent:'start',
        alignItems:'center',
        cursor:'pointer',
        transition:'.3s ease',
        
        '&:hover':{
            background:'rgba(253, 114, 114,0.2)',
            transform:'translateY(-4%)',
            boxShadow: 'rgba(0, 0, 0, 0.3) 0px 0px 0px, rgba(0, 0, 0, 0.22) 0px 5px 5px'
        }
    }
})


export default function ListTodo({asset, active}){
    const classes = useStyle();

    //-------function-----------//
    
    return(
        <Paper spacing={2} className={classes.root} >
            <CheckBox
                style={{width:'20%'}}
                checked={asset.isCompleted}
                onClick={()=> active(asset.id, asset.todo, asset.isCompleted)}
            />
            <Typography style={{width:'80%'}} align='left'  children={<span>{asset.todo}</span>} />
            <Typography style={{width:'100%'}} align='center'  children={<span>{asset.date}</span>} />
        </Paper>
    )
}
