import { Box, InputBase, TextField, makeStyles, Input } from "@material-ui/core"
import CheckIcon from '@material-ui/icons/Check';
import React from "react";

const useStyle = makeStyles({
    iconActive:{
        color:'#55efc4'
    },
    iconDisable:{
        color:'#b2bec3'
    }
})
export default function EditTodo(){
    const [newTodo, setNewTodo] = React.useState("");
    const classes = useStyle();
    return(
        <Box spacing={2}>
            <div>
                <TextField
                    style={{width:'80%'}}
                    defaultValue='Todoo'
                    label='Nhiệm vụ mới'
                    onChange = {(e)=> setNewTodo(e.target.value)}
                />            
                <CheckIcon color='secondary' className={newTodo === '' ? `${classes.iconDisable}` : `${classes.iconActive}`} />
            </div>
            <form className={classes.container} noValidate>
                                <TextField
                                    id="datetime-local"
                                    label="Chọn giới hạn"
                                    type="datetime-local"
                                    value=''                                    
                                    // onMouseLeave={() => setChecked(false)}                               
                                    InputLabelProps={{
                                        shrink: true,                                       
                                    }}                                    
                                    // onChange={e => setDate(e.target.value)}
                                />
            </form> 
            <TextField color='secondary' fullWidth='true' label='Ghi chú' variant='outlined'/>
        </Box>
    )
}
