import { Grid, Box, Typography, Paper, InputBase, IconButton, makeStyles, Divider, ButtonGroup,TextField, Fade } from "@material-ui/core"
import CardTravelIcon from '@material-ui/icons/CardTravel';
import SaveIcon from '@material-ui/icons/Save';
import ListTodo from '../component/ListTodo';
import ListComplete from '../component/ListComplete';
import EventIcon from '@material-ui/icons/Event';
import React from 'react'
import EditTodo from '../component/EditTodo'
import { set } from "date-fns";
const useStyle= makeStyles({
    root:{
        width:'350px',
        padding: '5px 10px',
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        boxShadow: 'rgba(252, 66, 123,0.5) 0px 0px 0px 3px'
    },
    container:{
        backgroundColor: '#f1f2f6'
    }
    
})
export default function BoxTodo(){
    const classes = useStyle();

    //--------useState--------//
    const [task, setTask] = React.useState([]);
    const [taskComplete, setTaskCompleted] = React.useState([]);
    const [taskInput, setTaskinput] = React.useState('');
    const [checked, setChecked] = React.useState(false);
    const [date, setDate]= React.useState("");
    const [edit, setEdit]=React.useState('');
    //--------Function-------//
    const openEdit =(id)=>{
        var data = [];
        task.map(loop => {
            if(loop.id == id){
                data.push(loop.todo);
            }
        })
        setEdit(data);
    }
    const addList = ()=>{
        setTask([ {id:task.length +1, todo:taskInput,date: date ,isCompleted:false},...task]);
        setTaskinput("");
        setDate('');
    }

    const delTodo = (id)=>{
        console.log('function',id);
        taskComplete.map(value=>{
            if(value.id == id){
                taskComplete.splice(taskComplete.indexOf(value),1);
                return setTaskCompleted([...taskComplete]);       
            }
        })
        
    }

    const checkComplete = (id,todo, isCompleted, date) => {
        if(isCompleted){
            taskComplete.forEach(value => {
                if(value.id == id){
                    taskComplete.splice(taskComplete.indexOf(value),1);
                    setTask([...task,{id: task.length+ 1, todo: todo, date:date  ,isCompleted:false }])
                }
            })
        }else{
            task.forEach(value=>{
                if(value.id == id){
                    task.splice(task.indexOf(value),1);
                    setTaskCompleted([...taskComplete,{id:taskComplete.length+1, todo: todo,date: date, isCompleted:true}])
                }
            })
        }
    }
    //----------Render--------//
    return(
        <Grid item >
            <Box position='relative'>
                <Typography color='secondary' align='center' paragraph='true' compornent='h1' variant='h5' children={<span><CardTravelIcon/> Todo List</span>}/>
                <Paper className={classes.root}>
                    <InputBase
                        placeholder='Danh sách việc cần làm'                        
                        style={{width:'90%'}}
                        defaultValue="2017-05-24T10:30"
                        inputProps={{ 'aria-label': 'todo list' }}
                        value={taskInput}                 
                        onChange={(e) => setTaskinput(e.target.value)}      
                    />
                    <ButtonGroup type='time' className={classes.btn}>
                        <IconButton color='secondary'  onClick={()=> setChecked(pre => !pre)}>
                            <EventIcon />
                        </IconButton>
                        <IconButton color='secondary'   onClick={addList} disabled={!taskInput}>
                            <SaveIcon/>
                        </IconButton>                        
                    </ButtonGroup>
                    <Fade in={checked}>
                        <Box 
                            position='absolute'
                            top='100px'
                            right='70px'
                        >
                            <form className={classes.container} noValidate>
                                <TextField
                                    id="datetime-local"
                                    label="Next appointment"
                                    type="datetime-local"
                                    value={date}                                    
                                    onMouseLeave={() => setChecked(false)}                               
                                    InputLabelProps={{
                                        shrink: true,                                       
                                    }}                                    
                                    onChange={e => setDate(e.target.value)}
                                />
                            </form>
                        </Box>
                    </Fade>
                </Paper>
                <Typography align='right' children={<span>{task.length} / {task.length + taskComplete.length}</span>} component='p'/>
                <Fade in={checked} children={<EditTodo asset/>}/>
                <Box>
                    {task.map(e =>( 
                        <Paper >
                            <ListTodo asset={e} key={e.id} active={checkComplete} openEdit={openEdit}/>
                        </Paper>
                    ))}
                </Box>
                <Divider light/>
                <Box>
                    {taskComplete.map(e => (
                        <Paper >
                            <ListComplete asset={e} key={e.id} active={checkComplete}  delCompleted={delTodo}/>
                        </Paper>
                    ))}
                </Box>
            </Box>
        </Grid>
    )
}
