import { Paper, Typography, makeStyles, IconButton } from "@material-ui/core";
import CheckBox from "@material-ui/core/Checkbox";
import React from 'react'
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';
const useStyle = makeStyles({
    root:{
        width:'100%',
        margin:'5px 0',
        display:'flex',
        justifyContent:'start',
        alignItems:'center',
        cursor:'pointer',
        transition:'.3s ease',
        
        '&:hover':{
            background:'rgba(253, 114, 114,0.2)',
            transform:'translateY(-4%)',
            boxShadow: 'rgba(0, 0, 0, 0.3) 0px 0px 0px, rgba(0, 0, 0, 0.22) 0px 5px 5px'
        }
    }
})


export default function ListTodo({asset, active, deleteTodo}){
    const classes = useStyle();
    const [checked,setChecked] = React.useState(false)

    //-------function-----------//
    
    return(
        <Paper spacing={2} className={classes.root} onClick={()=> active(asset.id, asset.todo, asset.isCompleted)}>
            <CheckBox
                checked={asset.isCompleted}
                onChange={()=> setChecked(pre => !pre)} 
            />
            <Typography style={{width:'80%'}} align='left'  children={asset.todo} />
            <IconButton >
                <DeleteOutlineIcon onClick={deleteTodo(asset.id,asset.isCompleted)}/>
            </IconButton>
        </Paper>
    )
}
