import { Grid, Box, Typography, Paper, InputBase, IconButton, makeStyles, Divider, Input, Button, Checkbox } from "@material-ui/core"
import CardTravelIcon from '@material-ui/icons/CardTravel';
import SaveIcon from '@material-ui/icons/Save';
import ListTodo from '../component/ListTodo';
import ListComplete from '../component/ListComplete';
import EventIcon from '@material-ui/icons/Event';
import React from 'react'

const useStyle= makeStyles({
    root:{
        width:'350px',
        padding: '5px 10px',
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        boxShadow: 'rgba(252, 66, 123,0.5) 0px 0px 0px 3px'
    }
})
export default function BoxTodo(){
    const classes = useStyle();

    //--------useState--------//
    const [task, setTask] = React.useState([]);
    const [taskComplete, setTaskCompleted] = React.useState([]);
    const [taskInput, setTaskinput] = React.useState('');

    //--------Function-------//
    const addList = ()=>{
        setTask([ {id:task.length +1, todo:taskInput, isCompleted:false},...task]);
        setTaskinput("");
    }

    const delTodo = (id)=>{
        console.log('function',id);
        taskComplete.map(value=>{
            if(value.id == id){
                taskComplete.splice(taskComplete.indexOf(value),1);
                return setTaskCompleted([...taskComplete]);       
            }
        })
        
    }

    const checkComplete = (id,todo,isCompleted) => {
        if(isCompleted){
            taskComplete.forEach(value => {
                if(value.id == id){
                    taskComplete.splice(taskComplete.indexOf(value),1);
                    setTask([...task,{id: task.length+ 1, todo: todo, isCompleted:false }])
                }
            })
        }else{
            task.forEach(value=>{
                if(value.id == id){
                    task.splice(task.indexOf(value),1);
                    setTaskCompleted([...taskComplete,{id:taskComplete.length+1, todo: todo, isCompleted:true}])
                }
            })
        }
    }
    //----------Render--------//
    return(
        <Grid item >
            <Box >
                <Typography color='secondary' align='center' paragraph='true' compornent='h1' variant='h5' children={<span><CardTravelIcon/> Todo List</span>}/>
                <Paper className={classes.root}>
                    <InputBase
                        placeholder='Danh sách việc cần làm'
                        style={{width:'90%'}}
                        inputProps={{ 'aria-label': 'todo list' }}
                        value={taskInput}                 
                        onChange={(e) => setTaskinput(e.target.value)}      
                    />
                    <Button type='time'>
                        <EventIcon/>
                    </Button>
                    <IconButton color='secondary' onClick={addList} disabled={!taskInput}>
                        <SaveIcon/>
                    </IconButton>
                </Paper>
                <Typography align='right' children={<span>{task.length} / {task.length + taskComplete.length}</span>} component='p'/>
                <Box>
                    {task.map(e =>( 
                        <Paper >
                            <ListTodo asset={e} key={e.id} active={checkComplete}/>
                        </Paper>
                    ))}
                </Box>
                <Divider light/>
                <Box>
                    {taskComplete.map(e => (
                        <Paper >
                            <ListComplete asset={e} key={e.id} active={checkComplete}  delCompleted={delTodo}/>
                        </Paper>
                    ))}
                </Box>
            </Box>
        </Grid>
    )
}
