import { Grid, Box, Typography, Paper, InputBase, IconButton, makeStyles, Divider } from "@material-ui/core"
import CardTravelIcon from '@material-ui/icons/CardTravel';
import SaveIcon from '@material-ui/icons/Save';
import ListTodo from '../component/ListTodo';
import React from 'react'

const useStyle= makeStyles({
    root:{
        width:'350px',
        padding: '5px 10px',
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        boxShadow: 'rgba(252, 66, 123,0.5) 0px 0px 0px 3px'
    }
})
export default function BoxTodo(){
    const classes = useStyle();

    //--------useState--------//
    const [task, setTask] = React.useState([]);
    const [taskInput, setTaskinput] = React.useState('');

    //--------Function-------//
    const addList = ()=>{
        setTask([...task, {id:task.length +1, todo:taskInput, isCompleted:false}]);
        setTaskinput("");
    }

    const checkComplete = (e) => {
        setTask(task.map(value => e.target.value === value.id ? {...value,isCompleted: true} : value));
    }
    //----------Render--------//
    return(
        <Grid item >
            <Box >
                <Typography color='secondary' align='center' paragraph='true' compornent='h1' variant='h5' children={<span><CardTravelIcon/> Todo List</span>}/>
                <Paper className={classes.root}>
                    <InputBase
                        placeholder='Danh sách việc cần làm'
                        style={{width:'90%'}}
                        inputProps={{ 'aria-label': 'todo list' }}
                        value={taskInput}                 
                        onChange={(e) => setTaskinput(e.target.value)}      
                    />
                    <IconButton onClick={addList}>
                        <SaveIcon color='secondary' />
                    </IconButton>
                </Paper>
                <Divider light/>
                <Box>
                    {task.map(value =>( 
                        <Box onClick={checkComplete} value={value.id}>
                            {console.log("ROOOT: ", value.isCompleted)}
                            <ListTodo asset={value} key={value.id}/>
                        </Box>
                    ))}
                </Box>
            </Box>
        </Grid>
    )
}
